import Form from './components/Form';
import Weather from './components/Weather';

const API_KEY = "10e43d3bf2f757a47fb74b83e31d4bf2";

//http://api.openweathermap.org/data/2.5/weather?q=cairo,egypt&appid=e36ed364400282e43250b6c4c0274d44

class App extends Component {

  getWeather = async (e) => {
    e.preventDefault();

    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    const api= await fetch('http://api.openweathermap.org/data/2.5/weather?q=cairo,egypt&appid=e36ed364400282e43250b6c4c0274d44')
    const data = await api.json();
  }


  render() {
    return (
      <div className="App">
        <Form getWeather={this.getWeather}/>
        <Weather />
      </div>
    );
  }
}

export default App;
